$("#contactForm").validator().on("submit", function (event) {
    if (event.isDefaultPrevented()) {
        // handle the invalid form...
        formError();
        submitMSG(false, "Hai riempito il form correttamente?");
    } else {
        // everything looks good!
        event.preventDefault();
    }
});

function sendEmail(){
    var name = $("#name").val();
    var email = $("#email").val();
    var msg_subject = $("#msg_subject").val();
    var msg_service = $("#msg_service").val();
    var message = $("#message").val();

    console.log(name);
    console.log(email);
    console.log(msg_subject);
    console.log(msg_service);
    console.log(message);
   
    email_body = `
        <table border="1">
        <tr>
            <td><h2>Richiesta da:</h2></td>
            <td><h2>#nome</h2></td>
        </tr>
        <tr>
            <td><h2>Email:</h2></td>
            <td><h2>#email</h2></td>
        </tr>
        <tr>
            <td><h2>Servizio:</h2></td>
            <td><h2>#servizio</h2></td>
        </tr>
        <tr>
            <td><h2>Oggetto:</h2></td>
            <td><h2>#oggetto</h2></td>
        </tr>
        <tr>
            <td><h2>Richiesta:</h2></td>
            <td><h2><pre>#richiesta</pre></h2></td>
        </tr>
    </table>`
    .replace("#nome", name)
    .replace("#email", email)
    .replace("#servizio", msg_service)
    .replace("#oggetto", msg_subject)
    .replace("#richiesta", message);
    
    console.log(email_body);
    if(message == '' || name == '' || email == '' || msg_subject == '' || msg_service == null){
        alert("Per favore compilare tutti i campi per proceedre con l'invio!!!");
        console.log("Errore: non tutti i campi sono stati compilati");
    }
    else{
        Email.send({
            SecureToken : '0dbd587a-5b00-4cca-b1ef-8d25e73fda99',
            To : 'ats@istitutoagnelli.it',
            From : 'ats@istitutoagnelli.it',
            Subject : msg_subject,
            Body : email_body
        }).then(
            message => {
                log_access("email");
                alert("Messaggio inviato con successo! Grazie");
                console.log("Messaggio inviato con successo!");
                $("#name").val("");
                $("#email").val("");
                $("#msg_subject").val("");
                $("#msg_service").val("");
                $("#message").val("");
            }   
        ); 
    }
}

function formSuccess(){
    $("#contactForm")[0].reset();
    submitMSG(true, "Message Submitted!")
}

function formError(){
    $("#contactForm").removeClass().addClass('shake animated').one('webkitAnimationEnd mozAnimationEnd MSAnimationEnd oanimationend animationend', function(){
        $(this).removeClass();
    });
}

function submitMSG(valid, msg){
    if(valid){
        var msgClasses = "h3 text-left tada animated text-success";
    } else {
        var msgClasses = "h3 text-left text-danger";
    }
    $("#msgSubmit").removeClass().addClass(msgClasses).text(msg);
}