$("#contactForm").validator().on("submit", function (event) {
    if (event.isDefaultPrevented()) {
        // handle the invalid form...
        formError();
        submitMSG(false, "Did you fill in the form properly?");
    } else {
        // everything looks good!
        event.preventDefault();
        submitForm();
    }
});

function sendEmail(){
    var name = $("#name").val();
    var email = $("#email").val();
    var msg_subject = $("#msg_subject").val();
    var message = $("#message").val();

    console.log(name);
    console.log(email);
    console.log(msg_subject);
    console.log(message);
   
    email_body = 'Email inviata da ' + name + ' [' + email + ']<br />' + message;
    console.log(email_body);
    if(message == '' || name == '' || email == '' || msg_subject == ''){
        alert("Errore durante l'invio del messaggio, riprovare");
        console.log("Message sent!");
    }
    else{
        Email.send({
            SecureToken : '0dbd587a-5b00-4cca-b1ef-8d25e73fda99',
            To : 'ats@istitutoagnelli.it',
            From : 'ats@istitutoagnelli.it',
            Subject : msg_subject,
            Body : email_body
        }).then(
          message => {
            alert("Messaggio inviato con successo! Grazie");
            console.log("Error to send the messagge");
            $("#name").val("");
            $("#email").val("");
            $("#msg_subject").val("");
            $("#message").val("");
        }   
            ); 
    }
        
       
    
}


function submitForm(){
    // Initiate Variables With Form Content
    var name = $("#name").val();
    var email = $("#email").val();
    var msg_subject = $("#msg_subject").val();
    var message = $("#message").val();


    $.ajax({
        type: "POST",
        url: "assets/php/form-process.php",
        data: "name=" + name + "&email=" + email + "&msg_subject=" + msg_subject + "&message=" + message,
        success : function(text){
            if (text == "success"){
                formSuccess();
            } else {
                formError();
                submitMSG(false,text);
            }
        }
    });
}

function formSuccess(){
    $("#contactForm")[0].reset();
    submitMSG(true, "Message Submitted!")
}

function formError(){
    $("#contactForm").removeClass().addClass('shake animated').one('webkitAnimationEnd mozAnimationEnd MSAnimationEnd oanimationend animationend', function(){
        $(this).removeClass();
    });
}

function submitMSG(valid, msg){
    if(valid){
        var msgClasses = "h3 text-left tada animated text-success";
    } else {
        var msgClasses = "h3 text-left text-danger";
    }
    $("#msgSubmit").removeClass().addClass(msgClasses).text(msg);
}